def item():

    string = ""

    if(random.random()<0.3):

        string += itemEffect[random.randint(0,len(itemEffect)-1)]+" "

    string +=qualityType[random.randint(0,len(qualityType)-1)]+" "+materialType[random.randint(0,len(materialType)-1)]+" "+itemType[random.randint(0,len(itemType)-1)]

    return string

def name_assist(titleVar, title, adj, name):

    if(random.random() < 0.3):
        title2 = title+random.randint(1,titleVar)
        if(random.random()<0.5):
            title2 = -title2
            
        string = name+"'s "+lesser[random.randint(0,len(lesser)-1)]+" "+properTitles[title2]+", "+firstNames[random.randint(0,len(firstNames)-1)]
    
    else:
        if(random.random() < 0.5):
            adj += random.randint(1,5)
        else:
            adj -= random.randint(1,5)
            
        string = name+"'s "+adjectives[adj]+" "+lesser[random.randint(0,len(lesser)-1)]+", "+firstNames[random.randint(0,len(firstNames)-1)]
    
    return string

def name():
    titleVar = 4;
    title = random.randint(titleVar,len(properTitles)-1-titleVar)
    adj = random.randint(5,len(adjectives)-6)

    string = firstNames[random.randint(0,len(firstNames)-1)]+" the "+adjectives[adj]+" "+properTitles[title]+" from "+sylable_name(6,countrySylables)
    string += "\n\tcarrying: "+item()
    string += "\n\t          "+item()
    string += "\n    "+name_assist(titleVar, title, adj, string.partition(' ')[0])
    string += "\n    "+name_assist(titleVar, title, adj, string.partition(' ')[0])
    string += "\n    "+name_assist(titleVar, title, adj, string.partition(' ')[0])
    string += "\n    "+name_assist(titleVar, title, adj, string.partition(' ')[0])
    string += "\n    "+name_assist(titleVar, title, adj, string.partition(' ')[0])
    string += "\n"
    
    return string

def area_prefix():

    def lt(name,s):
        tot = 0
        for c in s:
            if(name.find(c)>=0):
                tot+=1
        return tot
    
    name = ""

    while(True):

        name = sylable_name(1, countrySylables)[:4]

        if( not(name == "" or name == " " or name[:3]=="For") and lt(name,'lougrmnbd') > 2):
            break;

    if(random.random()<0.3):
        name = name[:3]

    return name

def descriptive_area(length=6):

    placeName = sylable_name(length, countrySylables)
    if(random.random() < 0.5):
        placeName=area_prefix()+" "+placeName
    if(random.random() < 0.2):
        placeName += " "+sylable_name(length, countrySylables)

    def name_split(n):
        n = n.split()
        string = n[0]
        n = n[1:]
        for s in n:
            if(random.random() < 0.15):
                string += " of "+s
            elif(random.random() < 0.8):
                string += " in "+s
            elif(random.random() < 0.6):
                string += "-"+s
            elif(random.random() < 0.6):
                string += " "+s
            else:
                string += ", "+s
        return string

    placeName2 = name_split(placeName)

    name = areaAdj[random.randint(0,len(areaAdj)-1)]

    def set_piece():

        name = materialType[random.randint(0,len(materialType)-1)]+" "+areaSetPiece[random.randint(0,len(areaSetPiece)-1)]
        return name

    def rnd_name(n):
        return n[random.randint(0,len(n)-1)]

    def assist(lead = False):

        split = placeName.split()

        if(not(lead)):
            name =  rnd_name(split) + "'s "
            name += areaAdj[random.randint(0,len(areaAdj)-1)]
            name += " " + areaBiome[random.randint(24,len(areaBiome)-1)]
            return name
        else:
            name = "Leads to: " + rnd_name(split) + "'s "
            name += areaAdj[random.randint(0,len(areaAdj)-1)]
            name += " " + areaBiome[random.randint(0,23)]
            return name

    name += " " + areaBiome[random.randint(0,23)]
    name += " of " + placeName2
    name += "\n\t" + assist()
    name += "\n\t\t" + set_piece()
    name += "\n\t" + assist()
    name += "\n\t\t" + set_piece()
    name += "\n\t" + assist(True)
    name += "\n\t\t" + assist()
    name += "\n\t\t\t" + set_piece()
    name += "\n\t\t" + assist()
    name += "\n\t\t\t" + set_piece()
    name += "\n\t" + assist(True)
    name += "\n\t\t" + assist()
    name += "\n\t\t\t" + set_piece()
    name += "\n\t\t" + assist()
    name += "\n\t\t\t" + set_piece()+"\n"

    return placeName2,name

def random_noun(min_length = 7):

    s = nouns[random.randint(0,len(nouns))-1]
    if( len(s) >= min_length):
        return s
    else:
        return random_noun(min_length)

